import React, {useState,useEffect} from 'react';
import './App.css';
import Navbar from './components/Navbar';
import Activities from './components/Activities';
import Rewards from './components/Rewards';
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";

function App() {
  const name ="Name: Akshata Dudhane"
  const dob="DOB: 04/05/2001"
  const tier="Tier: GOLD"
  const [purchases, setPurchases] = useState([]); // Define purchases state
  const [totalPurchaseCount, setTotalPurchaseCount] = useState(0);

  useEffect(() => {
    // Calculate the total purchase count based on the purchases data
    const totalCount = purchases.reduce((acc, purchase) => acc + purchase.count, 0);
    setTotalPurchaseCount(totalCount);
  }, [purchases]);


  return (
    <>
     <Router>
        <Navbar totalPurchaseCount={totalPurchaseCount} name={name} dob={dob} tier={tier}/>
        <div>
          <Routes>
          <Route path="/" element={<Activities />} />
          <Route path="/list" element={<Rewards />} />
         </Routes>
        </div>
      </Router>

    </>
  );
}

export default App;
