import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './Activities.module.css'


export default function Activities() {
  const [purchases, setPurchases] = useState([]);
  const URL = 'https://65127e22b8c6ce52b395b28b.mockapi.io/api/v1/activity';


  const handlePurchaseClick = (purchaseId) => {
    const updatedPurchases = purchases.map((purchase) => {
      if (purchase.id === purchaseId) {
        const updatedPurchase= { ...purchase, count: purchase.count + 1 };
        localStorage.setItem('purchase_${purchase.id}', JSON.stringify(updatedPurchase));
        return updatedPurchase;
      }
      return purchase; 

    });

    setPurchases(updatedPurchases);

  };


  useEffect(() => {
    const fetchPurchases = async () => {
      try {
        const {data} = await axios.get(URL);
        const storedPurchases =data.map((purchase) =>{
        const storedPurchase =localStorage.getItem('purchase_${purchase.id}');
        if(storedPurchase){
          return JSON.parse(storedPurchase);
        }
        return purchase;
        });
        setPurchases(storedPurchases);
        console.log(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchPurchases();
  }, []);

  return (
    <>
      <h2>Purchases</h2>
      <table className={styles.table}>
        <thead>
          <tr>
            <th>id</th>
            <th>Description</th>
            <th>Name</th>
            <th>Count</th>
            <th>Click to purchase</th>
          </tr>
        </thead>
        <tbody>
        {purchases.map((purchase) => (
  <tr key={purchase.id} onClick={() => handlePurchaseClick(purchase.id)}>
    <td>{purchase.id}</td>
    <td>{purchase.desc}</td>
    <td>{purchase.name}</td>
    <td>{purchase.count}</td>
    <td><button type="button" className="btn btn-dark">Purchase</button></td>
  </tr>
))}
        </tbody>
      </table>
    </>
  );
}
