import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './Activities.module.css'


export default function Activities() {
  const [purchases, setPurchases] = useState([]);
  const URL = 'https://65127e22b8c6ce52b395b28b.mockapi.io/api/v1/activity';


  const handlePurchaseClick =async (purchaseId) => {
    const updatedPurchases = purchases.find((purchase) => purchase.id === purchaseId);
      if (!updatedPurchases) {
        console.error('Purchase not found');
        return;

    }

    updatedPurchases.count+=1;
    try{
      await axios.put('https://65127e22b8c6ce52b395b28b.mockapi.io/api/v1/${purchaseId}',{
        count: updatedPurchases.count,
      });
console.log('successful purchase');
setPurchases([...purchases]);
    }
    catch(error){
      console.log('erroorr');
    }
  };

  useEffect(() => {
    const fetchPurchases = async () => {
      try {
        const {data} = await axios.get(URL);
        setPurchases(data);
        console.log(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchPurchases();
  }, []);

  return (
    <>
      <h2>Purchases</h2>
      <table className={styles.table}>
        <thead>
          <tr>
            <th>id</th>
            <th>Description</th>
            <th>Name</th>
            <th>Count</th>
            <th>Click to purchase</th>
          </tr>
        </thead>
        <tbody>
        {purchases.map((purchase) => (
  <tr key={purchase.id} onClick={() => handlePurchaseClick(purchase.id)}>
    <td>{purchase.id}</td>
    <td>{purchase.desc}</td>
    <td>{purchase.name}</td>
    <td>{purchase.count}</td>
    <td><button type="button" className="btn btn-dark">Purchase</button></td>
  </tr>
))}
        </tbody>
      </table>
    </>
  );
}
