import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './Activities.module.css';

export default function Activities() {
  const [purchases, setPurchases] = useState([]);
  const URL = 'https://65127e22b8c6ce52b395b28b.mockapi.io/api/v1/activity';

  const handlePurchaseClick = async (purchaseId) => {
    const updatedPurchases = purchases.map((purchase) => {
      if (purchase.id === purchaseId) {
        return { ...purchase, count: purchase.count + 1 };
      }
      return purchase;
    });

    setPurchases(updatedPurchases);

    try {
      // Prepare the updated purchase data to send to the API
      const updatedPurchaseData = {
        purchases: updatedPurchases,
      };

      // Send a PUT request to update the purchase data in the API
      await axios.put(URL, updatedPurchaseData);

      console.log('Purchase updated successfully in the API.');
    } catch (error) {
      console.error('Error updating purchase data in the API:', error);
    }
  };

  useEffect(() => {
    const fetchPurchases = async () => {
      try {
        const { data } = await axios.get(URL);
        setPurchases(data);
        console.log(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchPurchases();
  }, []);

  return (
    <>
      <h2>Purchases</h2>
      <table className={styles.table}>
        <thead>
          <tr>
            <th>id</th>
            <th>Description</th>
            <th>Name</th>
            <th>Count</th>
            <th>Click to purchase</th>
          </tr>
        </thead>
        <tbody>
          {purchases.map((purchase) => (
            <tr key={purchase.id} onClick={() => handlePurchaseClick(purchase.id)}>
              <td>{purchase.id}</td>
              <td>{purchase.desc}</td>
              <td>{purchase.name}</td>
              <td>{purchase.count}</td>
              <td>
                <button type="button" className="btn btn-dark">
                  Purchase
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}
