import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './Activities.module.css';

export default function Activities() {
  const [purchases, setPurchases] = useState([]);
  const baseURL = 'https://65127e22b8c6ce52b395b28b.mockapi.io/api/v1/';

  const handlePurchaseClick = async (purchaseId) => {
    // Find the purchase in the local state
    const purchaseToUpdate = purchases.find((purchase) => purchase.id === purchaseId);

    if (!purchaseToUpdate) {
      console.error('Purchase not found in local state.');
      return;
    }

    // Increment the count
    purchaseToUpdate.count += 1;

    try {
      // Send a PUT request to update the count in the API
      await axios.put(`${baseURL}/${purchases}${purchaseId}`, purchaseToUpdate);
      console.log('Purchase count updated successfully in the API.');
      // Update the local state with the modified purchase
      setPurchases([...purchases]); // Make sure to create a new array to trigger a re-render
    } catch (error) {
      console.error('Error updating purchase count in the API:', error);
    }
  };

  useEffect(() => {
    const fetchPurchases = async () => {
      try {
        const { data } = await axios.get(`${baseURL}/${activity}`);
        setPurchases(data);
        console.log(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchPurchases();
  }, [baseURL]);

  return (
    <>
      <h2>Purchases</h2>
      <table className={styles.table}>
        <thead>
          <tr>
            <th>id</th>
            <th>Description</th>
            <th>Name</th>
            <th>Count</th>
            <th>Click to purchase</th>
          </tr>
        </thead>
        <tbody>
          {purchases.map((purchase) => (
            <tr key={purchase.id} onClick={() => handlePurchaseClick(purchase.id)}>
              <td>{purchase.id}</td>
              <td>{purchase.desc}</td>
              <td>{purchase.name}</td>
              <td>{purchase.count}</td>
              <td>
                <button type="button" className="btn btn-dark">
                  Purchase
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}
