import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './Activities.module.css'


export default function Activities() {
  const [purchases, setPurchases] = useState([]);
  const apiUrl = 'https://65127e22b8c6ce52b395b28b.mockapi.io/api/v1/activity';
  const [count,setCount] = useState();
  const handlePurchaseClick = (purchaseId) => {
    // Find the clicked purchase in the purchases array
    const updatedPurchases = purchases.map((purchase) => {
      if (purchase.id === purchaseId) {
        // Increment the count by 1 (you can adjust this logic as needed)
        return { ...purchase, count: purchase.count + 1 };
      }
      return purchase; // Return unchanged purchases
    });

    // Update the state with the modified purchases array
    setPurchases(updatedPurchases);
  };
  useEffect(() => {
    const fetchPurchases = async () => {
      try {
        const response = await axios.get(apiUrl);
        setPurchases(response.data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchPurchases();
  }, []);

  return (
    <>
      <h2>Purchases</h2>
      <table className={styles.table}>
        <thead>
          <tr>
            <th>id</th>
            <th>Description</th>
            <th>Name</th>
            <th>Count</th>
            <th>Click to purchase</th>
          </tr>
        </thead>
        <tbody>
          {purchases.map((purchase)  => (
            <tr key={purchase.id} onClick={incrementCounter}>
              <td>{purchase.id}</td>
              <td>{purchase.desc}</td>
              <td>{purchase.name}</td>
              <td>{purchase.count}</td>
              <td><button type="button"  className="btn btn-dark">Purchase</button></td>
            </tr>
          ))}
        </tbody>
      </table>
    </>
  );
}
