import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './Activities.module.css'


export default function Activities() {
  const [purchases, setPurchases] = useState([]);
  const URL = 'https://65127e22b8c6ce52b395b28b.mockapi.io/api/v1/activity';


  // const handlePurchaseClick = (purchaseId) => {
  //   const updatedPurchases = purchases.map((purchase) => {
  //     if (purchase.id === purchaseId) {
  //       const updatedPurchase = { ...purchase, count: purchase.count + 1 };
  //       // Store the updated purchase in localStorage with a unique key
  //       localStorage.setItem(`purchase_${purchase.id}`, JSON.stringify(updatedPurchase));
  //       return updatedPurchase;
  //     }
  //     return purchase;
  //   });

  //   setPurchases(updatedPurchases);
  // };

  const [count, setCount] = useState(parseInt(localStorage.getItem('count')) || 0);

  // Update localStorage whenever the state changes
  useEffect(() => {
    localStorage.setItem('count', count.toString());
  }, [count]);

  const increment = () => {
    setCount(count + 1);
  };

  useEffect(() => {
    const fetchPurchases = async () => {
      try {
        const {data} = await axios.get(URL);
        setPurchases(data);
        console.log({data});
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchPurchases();
  }, []);

  return (
    <>
      <h2>Purchases</h2>
      <table className={styles.table}>
        <thead>
          <tr>
            <th>id</th>
            <th>Description</th>
            <th>Name</th>
            <th>Count</th>
            <th>Click to purchase</th>
          </tr>
        </thead>
        <tbody>
        {purchases.map((purchase) => (
  <tr key={purchase.id}>
    <td>{purchase.id}</td>
    <td>{purchase.desc}</td>
    <td>{purchase.name}</td>
    <td>{purchase.count}</td>
    <td><button onClick={increment}  type="button" className="btn btn-dark">Purchase</button></td>
  </tr>
))}
        </tbody>
      </table>
    </>
  );
}
