import React, { useState, useEffect } from 'react';
import './App.css';
import Navbar from './components/Navbar';
import Activities from './components/Activities';
import axios from 'axios';
import Rewards from './components/Rewards';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';

function App() {
  const name = "Name: Akshata Dudhane";
  const dob = "DOB: 04/05/2001";
  const tier = "Tier: GOLD";
  const [purchases, setPurchases] = useState([]); // State to store purchase data
  const [totalPurchaseCount, setTotalPurchaseCount] = useState(0); // State to store the total count

  useEffect(() => {
    // Fetch purchases data from the API
    const fetchPurchases = async () => {
      try {
        const response = await axios.get('https://65127e22b8c6ce52b395b28b.mockapi.io/api/v1/activity'); // Replace with your API URL
        const totalCount = response.data.reduce((acc, purchase) => acc + purchase.count, 0);
        setPurchases(totalCount);

        // Calculate the total count here

        // setTotalPurchaseCount(totalCount);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchPurchases();
  },[]);

  return (
    <>
      <Router>
        <Navbar totalPurchaseCount={totalPurchaseCount} name={name} dob={dob} tier={tier} />
        <div>
          <Routes>
            <Route path="/" element={<Activities purchases={purchases} />} />
            <Route path="/list" element={<Rewards />} />
          </Routes>
        </div>
      </Router>
    </>
  );
}

export default App;
