import React, { useState, useEffect } from 'react';
import axios from 'axios';
import styles from './Activities.module.css'


export default function Activities() {
  const [purchases, setPurchases] = useState([]);
  const [totalCount, setTotalCount] = useState(0);
  const URL = 'https://65127e22b8c6ce52b395b28b.mockapi.io/api/v1/activity';

  useEffect(() => {
    const fetchPurchases = async () => {
      try {
        const {data} = await axios.get(URL);
        setPurchases(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    };

    fetchPurchases();
  }, []);
  const updateTotalCount = (purchaseData) => {
    const updatedTotalCount = purchaseData.reduce((acc, purchase) => acc + purchase.count, 0);
    setTotalCount(updatedTotalCount);
  };
  const handlePurchaseClick = async (purchaseId) => {
    // Find the clicked purchase in the local state
    const updatedPurchases = purchases.map((purchase) => {
      if (purchase.id === purchaseId) {
        // Increment the count by 1 (you can adjust this logic as needed)
        return { ...purchase, count: purchase.count + 1 };
      }
      return purchase; // Return unchanged purchases
    });

    // Update the local state with the modified purchases
    setPurchases(updatedPurchases);
    updateTotalCount(updatedPurchases);

    // Prepare the updated purchase data to send to the API
    const updatedPurchaseData = {
      purchases: updatedPurchases,
    };

    try {
      // Send a PUT request to update the purchase data in the API
      const response = await axios.put(URL, updatedPurchaseData);

      // Check if the API response indicates success
      if (response.status === 200) {
        console.log('Purchase updated successfully.');
      } else {
        console.error('Purchase update failed. Status:', response.status);
      }
    } catch (error) {
      console.error('Error updating purchase data in the API:', error);
    }
  };

  return (
    <>
      <h2>Purchases</h2>
      <div className={styles.totalCount}>Total Count: {totalCount}</div>
      <table className={styles.table}>
        <thead>
          <tr>
            <th>id</th>
            <th>Description</th>
            <th>Name</th>
            <th>Count</th>
            <th>Click to purchase</th>
          </tr>
        </thead>
        <tbody>
        {purchases.map((purchase) => (
  <tr key={purchase.id} onClick={() => handlePurchaseClick(purchase.id)}>
    <td>{purchase.id}</td>
    <td>{purchase.desc}</td>
    <td>{purchase.name}</td>
    <td>{purchase.count}</td>
    <td><button type="button" className="btn btn-dark">Purchase</button></td>
  </tr>
))}
        </tbody>
      </table>
    </>
  );
}
